-- ----------------------------
-- Table structure for `character_counters`
-- ----------------------------
DROP TABLE IF EXISTS `character_counters`;
CREATE TABLE `character_counters` (
  `char_id` int(11) NOT NULL,
  `_highest_karma` int(11) NOT NULL DEFAULT '0',
  `_player_died` int(11) NOT NULL DEFAULT '0',
  `_players_ressurected` int(11) NOT NULL DEFAULT '0',
  `_duels_win` int(11) NOT NULL DEFAULT '0',
  `_mobs_killed` int(11) NOT NULL DEFAULT '0',
  `_raids_killed` int(11) NOT NULL DEFAULT '0',
  `_summons_killed` int(11) NOT NULL DEFAULT '0',
  `_champions_killed` int(11) NOT NULL DEFAULT '0',
  `_recipes_crafted` int(11) NOT NULL DEFAULT '0',
  `_recipes_failed` int(11) NOT NULL DEFAULT '0',
  `_longest_spree` int(11) NOT NULL DEFAULT '0',
  `_sprees_ended` int(11) NOT NULL DEFAULT '0',
  `_crits_done` int(11) NOT NULL DEFAULT '0',
  `_mcrits_done` int(11) NOT NULL DEFAULT '0',
  `_sieges_won` int(11) NOT NULL DEFAULT '0',
  `_fortresses_won` int(11) NOT NULL DEFAULT '0',
  `_dominion_won` int(11) NOT NULL DEFAULT '0',
  `_most_adena_had` bigint(22) NOT NULL DEFAULT '0',
  `_achievements_done` int(2) NOT NULL DEFAULT '0',
  `_seeds_extracted` int(11) NOT NULL DEFAULT '0',
  `_normal_enchant_success` int(11) NOT NULL DEFAULT '0',
  `_blessed_enchant_success` int(11) NOT NULL DEFAULT '0',
  `_become_hero` int(11) NOT NULL DEFAULT '0',
  `_fish_catched` int(11) NOT NULL DEFAULT '0',
  `_treasure_box_opened` int(11) NOT NULL DEFAULT '0',
  `_pvp_kills` int(11) NOT NULL DEFAULT '0',
  `_level_reached` int(11) NOT NULL DEFAULT '0',
  `_level_reached_subclass` int(11) NOT NULL DEFAULT '0',
  `_get_married` int(11) NOT NULL DEFAULT '0',
  `_enchant_item_on` int(11) NOT NULL DEFAULT '0',
  `_treasure_goblin_kills` int(11) NOT NULL DEFAULT '0',
  `_full_items_grade` int(11) NOT NULL DEFAULT '0',
  `_antharas_killed` int(11) NOT NULL DEFAULT '0',
  `_baium_killed` int(11) NOT NULL DEFAULT '0',
  `_valakas_killed` int(11) NOT NULL DEFAULT '0',
  `_orfen_killed` int(11) NOT NULL DEFAULT '0',
  `_ant_queen_killed` int(11) NOT NULL DEFAULT '0',
  `_core_killed` int(11) NOT NULL DEFAULT '0',
  `_beleth_killed` int(11) NOT NULL DEFAULT '0',
  `_sailren_killed` int(11) NOT NULL DEFAULT '0',
  `_baylor_killed` int(11) NOT NULL DEFAULT '0',
  `_participitate_in_main_events` int(11) NOT NULL DEFAULT '0',
  `_participitate_in_mini_events` int(11) NOT NULL DEFAULT '0',
  `_ctf_flags_captured` int(11) NOT NULL DEFAULT '0',
  `_ctf_returned_flags` int(11) NOT NULL DEFAULT '0',
  `_first_blood` int(11) NOT NULL DEFAULT '0',
  `_chests_opened` int(11) NOT NULL DEFAULT '0',
  `_killed_mutants` int(11) NOT NULL DEFAULT '0',
  `_killed_zombies` int(11) NOT NULL DEFAULT '0',
  `_domination_scored_points` int(11) NOT NULL DEFAULT '0',
  `_voted` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`char_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;